import React, { useState, useEffect } from 'react';
import './CourseRegistration2.css';

function CourseRegistration2() {
  const [courses, setCourses] = useState([]);
  const [registeredCourses, setRegisteredCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [selectedSemester, setSelectedSemester] = useState('Học Mới');

  useEffect(() => {
    fetch('https://example.com/api/courses')
      .then((response) => response.json())
      .then((data) => setCourses(data));
  }, []);

  const handleRegisterCourse = () => {
    if (!selectedCourse) {
      return;
    }

    const newRegisteredCourses = [...registeredCourses, selectedCourse];
    setRegisteredCourses(newRegisteredCourses);
  };

  const handleUnregisterCourse = (courseId) => {
    const newRegisteredCourses = registeredCourses.filter((course) => course.id !== courseId);
    setRegisteredCourses(newRegisteredCourses);
  };

  return (
    <div className="CourseRegistration2">
      <h1>Đăng ký học phần</h1>

      <div className="filters">
        <label>Đợt đăng ký:</label>
        <select value={selectedSemester} onChange={(event) => setSelectedSemester(event.target.value)}>
          <option value="Học Mới">Học Mới</option>
          <option value="Học Lại">Học Lại</option>
          <option value="Học Cải Thiện">Học Cải Thiện</option>
        </select>
      </div>

      <div className="courses">
        <h2>Môn học phân đang chờ đăng ký</h2>
        <table>
          <thead>
            <tr>
              <th>STT</th>
              <th>Mã môn học</th>
              <th>Tên môn học</th>
              <th>TC</th>
              <th>Học phần: học trước (4) tiên quyết</th>
              <th>Học phần tương đương</th>
            </tr>
          </thead>
          <tbody>
            {courses.map((course) => (
              <tr key={course.id}>
                <td>{course.id}</td>
                <td>{course.code}</td>
                <td>{course.name}</td>
                <td>{course.credits}</td>
                <td>{course.prerequisites}</td>
                <td>{course.equivalents}</td>
                <td>
                  <button onClick={() => setSelectedCourse(course)}>Đăng ký</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="registered-courses">
        <h2>Lớp học phân đã đăng ký trong học kỳ này</h2>
        <table>
          <thead>
            <tr>
              <th>STT</th>
              <th>Mã lớp học</th>
              <th>Tên môn học</th>
              <th>Lớp học dự kiến</th>
              <th>TC</th>
              <th>Nhóm</th>
              <th>Học phí</th>
              <th>Hạn nộp</th>
              <th>Thu Trang thái</th>
              <th>Ngày ĐK</th>
              <th>Trang Thai</th>
            </tr>
          </thead>
          <tbody>
            {registeredCourses.map((course) => (
              <tr key={course.id}>
                <td>{course.id}</td>
                <td>{course.code}</td>
                <td>{course.name}</td>
                <td>{course.class}</td>
                <td>{course.credits}</td>
                <td>{course.group}</td>
                <td>{course.tuition}</td>
                <td>{course.deadline}</td>
                <td>{course.status}</td>
                <td>{course.registrationDate}</td>
                <td>
                  <button onClick={() => handleUnregisterCourse(course.id)}>Hủy đăng ký</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CourseRegistration2;
